INSERT INTO `tbl_agenda` (`id_agenda`, `judul_agenda`, `ringkasan_agenda`, `isi_agenda`, `tgl_agenda`, `created_at`, `updated_at`) VALUES
(1, 'Pelatihan dan sertifikasi Tenaga Terampil Konstruksi', '<p>Pelatihan dan Sertifikasi Tenaga Terampil Konstruksi, 16 - 17 Januari 2019 Diselenggarakan Oleh Balai Material dan Peralatan Konstruksi Bekerjasama dengan PT. Taso bertempat di Balai Litbang Penerapan Teknologi Permukiman Yogyakarta</p>\r\n', '<p><b>Pelatihan dan Sertifikasi Tenaga Terampil Konstruksi</b><br />\r\nMemfasilitasi Kendaraan Mobile Training Unit Acara diselenggarakan oleh Balai Material dan Peralatan Konstruksi Bekerjasama dengan PT. Taso dengan Sub Klasifikasi Tukang Baja Ringan</p>\r\n\r\n<p>Tanggal 16 - 17 Januari 2019 bertempat di Balai Litbang Penerapan Teknologi Permukiman Yogyakarta</p>\r\n', '2019-01-16', '2022-03-06 18:09:58', '2022-03-06 18:09:58'),
(2, 'Pelatihan dan Sertifikasi Tenaga Terampil Konstruksi', '<p>Pelatihan dan Sertifikasi Tenaga Terampil Konstruksi, 28 Januari - 01 Februari 2019 Diselenggarakan Oleh Balai Wilayah IV Surabaya Bekerjasama dengan LP Wirogunan bertempat di Lembaga Pemasyarakatan Wirogunan Yogyakarta</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>Memfasilitasi Kendaraan Mobile Training Unit, Pelatihan dan Sertifikasi Tenaga Terampil Konstruksi, 28 Januari - 01 Februari 2019 Acara Diselenggarakan Oleh Balai Wilayah IV Surabaya Bekerjasama dengan LP Wirogunan bertempat di Lembaga Pemasyarakatan Wirogunan Yogyakarta</p>\r\n\r\n<p>&nbsp;</p>\r\n', '2019-01-28', '2022-03-06 18:09:58', '2022-03-06 18:09:58'),
